     * The generated access token is:
     * eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9..vrDlewPBqdOTp01NlW4tpAns8_phN2KExYEThaSjFw9OmGJVlEMWUQL_p6PHID0YFLzTQxRehNBaulEbgQaO2K7svaXMkwXRcVUgGudgDmJp-jooUpiMrbV9j1yxb9pMbJdtNco-Xp5yp2_ryce-kju2qQ_BS_drRWc8bEF83VXHSOQcZTW36-02uwzL23OLrjBlKSu44qV5VaFR63pWQ4IlUcOE2kcfujtX0AOdOaDoRYYJ_weNhaW6HNwLvDxCAkKcbqWYFVCjFWHsgoIQgdk3uHejl7LD0ay_ymSHhZxzx_PgKFfAYQDALji9_pXsbRMxCaSN-ZpKxBHQzLQjMA
     * 
     * So in this tests, we test our program to encode access token,
     * and check if access token generated by the program matches with expected access token above.
     * 
     * */

    public class AccessTokenGeneratorTests
    {
        [Theory]
        [InlineData(
            "",
            "TomcatSadis.AuthService",
            "TomcatSadis.AccessTokenHandler",
            "2020-11-07T01:02:03.0Z",
            31104000,
            "test123",
            "Albert",
            "Brucelee",
            "eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiJ0ZXN0MTIzIiwiZ2l2ZW5fbmFtZSI6IkFsYmVydCIsImZhbWlseV9uYW1lIjoiQnJ1Y2VsZWUiLCJuYmYiOjE2MDQ3MTA5MjMsImV4cCI6MTYzNTgxNDkyMywiaXNzIjoiVG9tY2F0U2FkaXMuQXV0aFNlcnZpY2UiLCJhdWQiOiJUb21jYXRTYWRpcy5BY2Nlc3NUb2tlbkhhbmRsZXIifQ.c7wf1jkZZQuqo6eyWDWKsJiluoeYCTdij3gzNm8kVaXm3hmZhgOTpfaXWm-fUMlYxQJLG1jDtYvEqIRlYAPnfQofpHiwWh72OfnIvuhPFZVIy_BiUpbz888BaZZTwqjvEI1XBBfu7UGTfIp8xb6xwcwOaHoO-VCxhOzw5vYe_tUS5a_G8k-_uINYtWJuselqpgYNMbOotb41_jMUgpr13-zhy3gPOwolDw-AxJt4RxNuDFdkfjHAfr1TxhGcZ8B9l5X7aQv2_myPqIRK9Hbp-ShShZ3hIeShNq1DToUECgEMdLK6CufdQCr6oMUeO-2xOl2zSOqN9GhWvdgXJvs3CQ")]
        public void GeneratedAccessTokenShouldBeValid(
            string privateKey,
            string issuer,
            string audience,
            string notBefore,
            double expiresIn,
            string userId,
            string givenName,
            string familyName,
            string expectedAccessToken)
        {
            var notBeforeDateTime = DateTime.Parse(notBefore);

            var additionalClaims = new List<Claim>
            {
                new Claim(JwtRegisteredClaimNames.GivenName, givenName),
                new Claim(JwtRegisteredClaimNames.FamilyName, familyName)
            };

            var accessToken = AccessTokenGenerator.GenerateAccessToken(
                userId: userId,
                privateKey: privateKey,
                issuer: issuer,
                audience: audience,
                notBefore: notBeforeDateTime,
                expires: notBeforeDateTime.AddSeconds(expiresIn),
                additionClaims: additionalClaims);

            Assert.Equal(expectedAccessToken, accessToken);
        }
    }
}
